
'use strict';document.addEventListener('DOMContentLoaded',function(){document.addEventListener('touchstart',function(){});var quantity=$('#quantity'),input=quantity.find('input[type="number"]'),btnUp=quantity.find('.quantity-up'),btnDown=quantity.find('.quantity-down'),min=input.attr('min'),max=input.attr('max'),step=input.attr('step');btnUp.click(function(){var oldValue=parseFloat(input.val());if(oldValue>=max){var newVal=oldValue;}else{var newVal=oldValue+parseInt(step);}
quantity.find("input").val(newVal);quantity.find("input").trigger("change");});btnDown.click(function(){var oldValue=parseFloat(input.val());if(oldValue<=min){var newVal=oldValue;}else{var newVal=oldValue-parseInt(step);}
quantity.find("input").val(newVal);quantity.find("input").trigger("change");});},false);